package reservation;

import cmn.DTO;

public class TempVO extends DTO{

	private int PNUM       ;
	private String ROOM    ;
	private int PERIOD  ;
	private String ICODE   ;
	private int PAY        ;
	private String UUID        ;
	private String TODAY   ;
	private String rcode   ;
	public TempVO() {
		// TODO Auto-generated constructor stub
	}
	public int getPNUM() {
		return PNUM;
	}
	public void setPNUM(int pNUM) {
		PNUM = pNUM;
	}
	public String getROOM() {
		return ROOM;
	}
	public void setROOM(String rOOM) {
		ROOM = rOOM;
	}
	public int getPERIOD() {
		return PERIOD;
	}
	public void setPERIOD(int pERIOD) {
		PERIOD = pERIOD;
	}
	public String getICODE() {
		return ICODE;
	}
	public void setICODE(String iCODE) {
		ICODE = iCODE;
	}
	public int getPAY() {
		return PAY;
	}
	public void setPAY(int pAY) {
		PAY = pAY;
	}
	public String getUUID() {
		return UUID;
	}
	public void setUUID(String uUID) {
		UUID = uUID;
	}
	public String getTODAY() {
		return TODAY;
	}
	public void setTODAY(String tODAY) {
		TODAY = tODAY;
	}
	public String getRcode() {
		return rcode;
	}
	public void setRcode(String rcode) {
		this.rcode = rcode;
	}
	@Override
	public String toString() {
		return "TempVO [PNUM=" + PNUM + ", ROOM=" + ROOM + ", PERIOD=" + PERIOD + ", ICODE=" + ICODE + ", PAY=" + PAY
				+ ", UUID=" + UUID + ", TODAY=" + TODAY + ", rcode=" + rcode + "]";
	}
	public TempVO(int pNUM, String rOOM, int pERIOD, String iCODE, int pAY, String uUID, String tODAY, String rcode) {
		super();
		PNUM = pNUM;
		ROOM = rOOM;
		PERIOD = pERIOD;
		ICODE = iCODE;
		PAY = pAY;
		UUID = uUID;
		TODAY = tODAY;
		this.rcode = rcode;
	}
	
	
}